import React,{useState} from "react";
import "./Lending.css";
import MainLogo from './static/Main LOGO.svg'
import './static/fonts/JockeyOne-Regular.ttf';
function Lending(props) {
    const [time, setTime] = useState(null)
    const updateTime = ()=>{
        setTime(new Date().toLocaleTimeString())
    }
    setInterval(updateTime)
    return (
        <div className="LendingContainer">
            <div>
                <img src={MainLogo} alt="NO IMG FOUND"></img>
                <div id="headingContainer">
                    <h1 id="headingText">Blockchain Lottery</h1>
                </div>
                <div id="pricepoolContainer">
                    <h1 id="pricepoolText">1000$</h1>
                </div>
                <div>
                    <div>
                        <p className="plain">Time To Roll : </p>
                        <p className="neonred">18:04:24</p>
                    </div>
                    <div>
                        <p className="plain">Daily Lottery : </p>
                        <p className="neonred">00:00 GMT</p>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Lending;
