import React from 'react'
import logo from './static/Header Logo.png'
import MetamaskButton from './static/Metamask btn.png'
import WalletConnectButton from './static/Wallet Connect btn.png'
import TronLinkButton from './static/Tron Link btn.png'
import HamBurger from './static/Hamburger.png'
import './static/fonts/JuraMedium.ttf';

import './Navbar.css'
function Navbar(props) {

  const toggleButton = document.getElementsByClassName('toggle-button')
  const navbarLinks = document.getElementsByClassName('navbar-links')

  toggleButton.addEventListener('click', () => {
    navbarLinks.classList.toggle('active')
  })
  return (
    <div className='navbarContainer'>
      <img className='hamburger' src={HamBurger} alt="NO IMG FOUND"></img>
      <nav className='navbar'>
        <img className='navLogo' src={logo} id="headerLogo" alt="NO IMG FOUND" style={{ height: "122px", width: "329px" }}></img>
        {props.accounts ? <p className='navAccountNumber'>{props.accounts[0].slice(0, 5) + ".." + props.accounts[0].slice(-4)}</p> :
          <div className='buttonContainers'>
            <img className='connectButtons' onClick={props.connectWallet} src={MetamaskButton} alt="NO IMG FOUND" style={{ height: "125px", width: "317.08px" }}></img>
            <img className='connectButtons' onClick={props.walletConnect} src={WalletConnectButton} alt="NO IMG FOUND" style={{ height: "125px", width: "317.08px" }}></img>
            <img className='connectButtons' onClick={props.tronWebConnect} src={TronLinkButton} alt="NO IMG FOUND" style={{ height: "125px", width: "317.08px" }}></img>
          </div>}
        {props.accounts ? <div className="usdtBalance">{props.usdtBalance} {props.symbol}</div> : null}
      </nav>
    </div>
  )
}

export default Navbar
